mapper.select:
  select

  from t_job_inst a
        left join t_ds_def b on a.job_id = b.ds_id
        left join t_job_def c on a.job_id=c.job_id
Where 1=1

      AND A.data_date=#{jobInst.dataDate}


      AND A.job_id=${jobInst.jobId}


      AND A.status = ${jobInst.status}

  order by a.data_date desc, a.start_time desc
  limit #{pageIndex}, #{pageSize}

select count(*)
from t_job_inst a
Where 1=1

    AND A.data_date=#{dataDate}


    AND A.job_id=${jobId}


    AND A.status = ${status}

select job_log
  from t_inst_log
where inst_id = #{instId}

